sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue == '') {
      return;
   }

	//retrieve value of When Needed choice in Variable and clear options
	var m_what_needed = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');

	// Load choices in u_when_needed based on requested type choice selection
	if(newValue == "facilities")
		{
			g_form.addOption('u_what_needed','fc1',"Facilities 1");
			g_form.addOption('u_what_needed','fc2',"Facilities 2");
			g_form.addOption('u_what_needed','other',"Other");
		}
	if(newValue == "hr")
		{
			g_form.addOption('u_what_needed','hr1',"Human Resource 1");
			g_form.addOption('u_what_needed','hr2',"Human Resource 2");
			g_form.addOption('u_what_needed','other',"Other");
		}
	if(newValue == "legal")
		{
			g_form.addOption('u_what_needed','lg1',"Legal 1");
			g_form.addOption('u_what_needed','lg2',"Legal 2");
			g_form.addOption('u_what_needed','other',"Other");
		}	



}
